function raspberry(vis, offSetX) {
  rasp = vis.append("g")
  data.drupelets.forEach(function (item) {
    rasp.append("path").attr("d", item)
      .attr("class", "drupelet")
  })
  data.leaves.forEach(function (item) {
    rasp.append("path").attr("d", item)
      .attr("class", "leaf")
  })

  rasp_width = rasp.node().getBBox().width
  rasp_height = rasp.node().getBBox().height

  recentre = `matrix(1 0 0 1 ${rasp_width / 2} ${rasp_height / 2})`
  reduce = `scale(${scale_factor}, ${scale_factor})`
  reposition = `translate(${x_offset}, ${y_offset})`

  rasp.attr('transform', d3.transform(
    `
    ${recentre}
    ${reposition}
    ${reduce}
    `).toString())
}
